function $(c){const t=new Date(c),o=Math.floor((new Date().getTime()-t.getTime())/1e3);if(o<0)return"in the future";const r={year:31536e3,month:2592e3,week:604800,day:86400,hour:3600,minute:60,second:1};for(const[s,a]of Object.entries(r)){const e=Math.floor(o/a);if(e>=1)return e===1?`1 ${s} ago`:`${e} ${s}s ago`}return"just now"}function l(c){const t=new Date(c),i=t.getFullYear(),o=t.getMonth()+1,r=t.getDate(),s=t.getHours(),a=t.getMinutes(),e=t.getSeconds(),d=t.getMilliseconds(),n=g=>g.toString().padStart(2,"0"),u=`${i}-${n(o)}-${n(r)}`,m=`${n(s)}:${n(a)}:${n(e)}`,f=(d*1e3).toString().padStart(6,"0");return`${u}T${m}.${f}`}export{l as c,$ as f};
